import React from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";

export default function Gallery() {
  const { register, handleSubmit, errors } = useForm(); // initialize the hook

  const onChange = (e) => {
    const file=e.target.files[0];
  }

  const onSubmit = (data) => {
    const storageRef= app.storage().ref();
    const fileRef=storageRef.child(data.image[0].name);
    fileRef.put(data.image[0]).then(()=>{
      console.log('upload a file');
    });
  };

  return (
    <Fade bottom cascade={true}>
    <div className="authContainer">
      <div className="form1">
        <form onSubmit={handleSubmit(onSubmit)}>
          <label><h3>Category</h3></label>
          <input name="category" ref={register} />
          <label><h3>Album</h3></label>
          <input name="name" ref={register({ required: true })} />
          {errors.name && "Name is required."}
          <label><h3>Upload</h3></label>
          <input name="picture" ref={register} type='file' name='picture' />
          {errors.age && "Please enter number for age."}
          <br></br>
          <button><h3>Submit</h3></button>
        </form>
        </div>
      </div>
    </Fade>
  );
}
