import React, { Fragment, useContext, useEffect, } from "react";
/* import styled, { css } from "styled-components"; */
import { Link } from "react-router-dom";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import styled, { css } from "styled-components";
import Fade from "react-reveal/Fade";
import MyContext from "../Context/MyContext.js";

export default function Fashion() {
  const { getData, setGetData } = useContext(MyContext);
  useEffect(() => {
    const getResult = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/albumup/bycategory?name=${'fashion'}`
        );
        const albums = await res.json();
        console.log(albums);
        setGetData(albums);
      } catch (err) {}
    };
    getResult();
  }, []);
/*   const Container = styled.div`
    position: relative;
    overflow: hidden;
    width: 50%;
    height: 20vh;
    margin: auto;
  `; */

  return (
    <Fragment>
      <Fade bottom cascade={true}>
        <div>
        <h1 className="VisionTitle">Fashion</h1>
        </div>

      </Fade>
      {/* <Link to="./fashionShow">
        <div className="visionsContainer">
          <div className="visionsDescriptions">
            <h3>Title</h3>
          </div>
          <PhotographyCarousel />
        </div>
      </Link> */}
      <Link to="./fashionShow">
      <div className="visionsContainer" onClick={goToAlbum}>
          <div className="visionsDescriptions">
            {getData &&
              getData.map((album) => {
                return (
                  <div>
                    <h3 key={album._id}>{album.albumName}</h3>
                    <Carousel
                      swipeable={true}
                      draggable={true}
                      showDots={false}
                      responsive={responsive}
                      infinite={true}
                      autoPlay={true}
                      autoPlaySpeed={1500}
                      keyBoardControl={true}
                      customTransition="all .5"
                      transitionDuration={500}
                      containerClass="carousel-container2"
                      removeArrowOnDeviceType={["tablet", "mobile", "desktop"]}
                      dotListClass="custom-dot-list-style"
                      itemClass="carousel-item-padding-40-px"
                    >
                      {album.images.map((url) => {
                        return <img src={url} key={url}/>;
                      })}

                    </Carousel>
                  </div>
                );
              })}
          </div>
        </div>
        </Link>
      <div className="Footer"></div>
    </Fragment>
  );
}
