import React, { useState, Fragment, useEffect, useContext } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Navigation from "./Components/Navigation";
import "./App.scss";
import About from "./Components/About";
import Collaborations from "./Components/Collaborations";
import Contacts from "./Components/Contacts";
import Visions from "./Components/Visions";
import SignIn from "./Components/SignIn";
import SoulsInABox from "./Components/SoulsInABox";
import Fashion from "./Components/Fashion";

import FashionShow from "./Components/FashionShow";
import VisionsShow from "./Components/VisionsShow";
import Home from "./Components/Home";
import Shop from "./Components/Shop";
import Gallery from "./Components/Gallery";
import ProductScreen from "./Components/ProductScreen";
import MyProvider from "./Context/MyProvider";
import PhotographyCarousel from "./Components/PhotographyCarousel";
import {
  ModalContext,
  ModalProvider,
  handleModal,
} from "./Components/modalContext";
import ScrollToTheTop from "./Components/ScrollToTop";
import { motion } from "framer-motion";
import CartScreen from "./Components/CartScreen";
import ShippingAddressScreen from "./Components/ShippingAddressScreen";
import Register from "./Components/Register";
import Payment from "./Components/Payment";
import Placeorder from "./Components/Placeorder";
import Order from "./Components/Order";
import OrderHistoryScreen from "./Components/OrderHistoryScreen";
import Profile from "./Components/Profile";
import PrivateRoute from "./Components/PrivateRoute";

export default function App() {
  const [show, setShow] = useState(false);
  const [showIcon, setShowIcon] = useState(false);

  const enterVariant = {
    opened: { fontSize: "5vw", y: 100, textAlign: "center" },
    closed: { visibility: "hidden" },
  };
  const logoVariant = {
    opened: { fontSize: "10vw", y: 300, textAlign: "center", opacity: 0.7 },
    closed: { y: -20, opacity: 1 },
  };

  return (
    <MyProvider>
      <ModalProvider>
        <Router>
          <ScrollToTheTop />
          <div className="header">
            {/* <div className="logo"> */}
            <motion.h2
              className="enter"
              initial={{ y: -250 }}
              transition={{ duration: 0.3, delay: 0.2 }}
              variants={enterVariant}
              animate={show ? "closed" : "opened"}
              onClick={() => setShow(!show)}
            >
              Enter
            </motion.h2>
            <motion.h1
              initial={{ y: -250 }}
              variants={logoVariant}
              transition={{ duration: 0.3, delay: 0.2 }}
              animate={show ? "closed" : "opened"}
              onClick={() => setShow(!show)}
            >
              The Naked Moon
            </motion.h1>
          </div>

          {show ? (
            <Fragment>
              <Navigation />

              <Switch>
                <Route exact path="/" component={Home} />
                <Route path="/About" component={About} />
                <Route exact path="/Fashion" component={Fashion} />
                <Route exact path="/Visions" component={Visions} />
                <Route exact path="/SoulsInABox" component={SoulsInABox} />
                <Route exact path="/FashionShow" component={FashionShow} />
                <Route exact path="/VisionsShow" component={VisionsShow} />
                <Route
                  exact
                  path="/Collaborations"
                  component={Collaborations}
                />
                <Route exact path="/Shop" component={Shop} />
                <Route exact path="/Shop/:id" component={ProductScreen} />
                <Route exact path="/Cart/:id?" component={CartScreen} />
                <Route exact path="/SignIn" component={SignIn} />
                <Route exact path="/Register" component={Register} />
                <Route
                  exact
                  path="/Shipping"
                  component={ShippingAddressScreen}
                />
                <Route exact path="/Payment" component={Payment} />
                <Route exact path="/Placeorder" component={Placeorder} />
                <Route exact path="/Order/:id" component={Order} />
                <Route exact path="/Contacts" component={Contacts} />
                <Route
                  exact
                  path="/orderhistory"
                  component={OrderHistoryScreen}
                ></Route>
                <PrivateRoute
                  path="/profile"
                  component={Profile}
                ></PrivateRoute>
              <Route exact path="gallery" component={Gallery} />
              </Switch>
            </Fragment>
          ) : (
            <div className="presentation"></div>
            /* <video autoPlay loop muted>
                <source src={Lightining} type="video/mp4" />
              </video>  */
          )}
          {/* </div> */}
        </Router>
      </ModalProvider>
    </MyProvider>
  );
}
