import React, { useEffect, Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { useLocation } from "react-router-dom";
import { ModalContext } from "./modalContext";
import useModal from "./useModal";
import Component from "./Component";
import ScrollToTheTop from "./ScrollToTop";

export default function FashionShow() {
  const { getData, setGetData } = useContext(MyContext);
  const location = useLocation();
  /* console.log(location);
  console.log(location.pathname); */
  /* const id=location.pathname */

  const params = new URLSearchParams(location.search);
  const id = params.get("name");
  console.log(id)
  let { handleModal } = React.useContext(ModalContext);

  useEffect(() => {
    const getResult = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/albumup/${id}`);
        const albums = await res.json();
        console.log(albums);
        setGetData(albums);
      } catch (err) {}
    };
    getResult();
  }, []);
  const ScrollUp = (url) => {
    handleModal(url);
    window.scrollTo(0, 0);
  };
  return (
    <Fragment>

      {getData &&
        getData.map((album) => {
          return (
            <div>
              <h1 className="VisionTitle">
                {album.albumName}
              </h1>
              <br></br>

              <div className="soulsContainer">
                {album.images.map((url) => {
                  return (
                    <img
                      src={url}
                      key={url}
                      onClick={() => ScrollUp(album.url_l)}
                      classname="soulsItems"
                    />
                  );
                })}
              </div>
            </div>
          );
        })}

    </Fragment>
  );
}
