import React, { useEffect, Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { useLocation } from "react-router-dom";
import { ModalContext } from "./modalContext";
/* import useModal from "./useModal";
import Component from "./Component";
import ScrollToTheTop from "./ScrollToTop"; */

export default function FashionShow() {
  const { getAlbum,setGetAlbum} = useContext(MyContext);
  const location = useLocation();
  console.log(location);
  const params = new URLSearchParams(location.search);

  const id = params.get("name");
  /* let { handleModal } = React.useContext(ModalContext); */
  console.log(id);
  useEffect(() => {
    const getResult = async () => {
      try {
        const url=`http://localhost:5000/api/albumup/${id}`
        const res = await fetch(url);
        console.log({url})
        const album = await res.json();
        console.log(album);
        setGetAlbum(album);
      } catch (err) {}
    };
    getResult();
  }, []);
  console.log(getAlbum);
  /*  const ScrollUp = (url) => {
    handleModal(url);
    window.scrollTo(0, 0);
  }; */
  return (
    <Fragment>
      {/* {getData.map((item) => {
        return ( */}
          <div>
            <h1 className="VisionTitle">{getAlbum && getAlbum.albumName}</h1>

            <br></br>
            {getAlbum && getAlbum.images.map((item) => {
              return (
                <div className="soulsContainer">
                  <img
                    src={item}
                    key={item}
                    /* onClick={() => ScrollUp(item)} */
                    classname="soulsItems"
                  />
                </div>
              );
            })}
          </div>
        {/* ); */}
     {/*  })} */}
    </Fragment>
  );
}
