import React, {Fragment, useEffect} from "react";
import {  useLocation } from "react-router-dom";
import Fade from "react-reveal/Fade";
import PhotographyCarousel from "./PhotographyCarousel";


export default function Category(props) {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get("name");
  useEffect  ( async ()=>{

    const res = await fetch(`http://localhost:5000/api/albumup/bycategory?name=${name}`)
    const albums = await res.json();
    console.log(albums);


  },[])

  const goToAlbum = () => {
    props.history.push(`/albumedit/`);
  };
  const goToCreateAlbum = () => {
    props.history.push(`/createalbum/`);
  };
  return (

    <Fragment>
      <Fade bottom cascade={true}>
        <div onClick={goToAlbum}>
        <h1 className="VisionTitle">Albums in <span style={{ color: "red" }} >{name}</span> category:<br></br> click on the album to edit </h1>

        </div>
        <div style={{ width:"100vw", display:"flex", flexFlow:"row", justifyContent:"center" }}>
        <button onClick={goToCreateAlbum} style={{ width:"30%" }}><h3>Add a new Album</h3></button>
        </div>



        <div className="visionsContainer" onClick={goToAlbum}>
          <div className="visionsDescriptions">
            <h3>Title</h3>
          </div>


          <PhotographyCarousel />
        </div>
        </Fade>
      </Fragment>
  );
}
