import React from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";

export default function Productup() {
  const { register, handleSubmit, errors } = useForm(); // initialize the hook

  const goToAlbum = () => {
    props.history.push(`/albumedit/`);
  };
  /* const onSubmit = async (data) => {
    if (data.image && data.image.length > 0) {
      data.image = data.image[0];
    }
    const formData = new FormData();
    for (let key in data) {
      formData.append(key, data[key]);
    }

    const res = await fetch("http://localhost:5000/api/Shop", {
      method: "POST",
      body: formData,
    });
    const fromApi = await res.json();
    alert(JSON.stringify(res));
    console.log(fromApi);
  }; */

  return (
    <Fade bottom cascade={true}>
      <div className="authContainer">
        <div className="form2">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label>
              <h3>Name</h3>
            </label>
            <input type="text" name="albumName" ref={register({ required: true })} />
            {errors.name && "Name is required."}
            <label>
                {/* It has to be defaoult */}
              <h3>Category</h3>
            </label>
            <input type="text" name="category" ref={register} />
            <label>
              <h3>Description</h3>
            </label>
            <textarea
              type="text"
              name="description"
              ref={register({ required: true })}
            />
            {errors.description && "Description is required."}

            <br></br>
            <button type="submit" onClick={goToAlbum} >
              <h3>Add photos</h3>
            </button>
          </form>
        </div>
      </div>
    </Fade>
  );
}
