import React, { useState } from "react";
import onClickOutside from "react-onclickoutside";

function Dropdown({ title, items = [], multiselect = false }) {
  const [open, setOpen] = useState(false);
  const [selection, setSelection] = useState([]);
  const toggle = setOpen(!open);
  Dropdown.handleClikOutside=()=>setOpen(false);

  function handleOnClick(item) {
    if (!selection.some((current) => current.id === item.id)) {
      if (!multiselect) {
        setSelection([item]);
      } else if (multiselect) {
        setSelection([...selection, item]);
      }
    } else {
      let selectionAfterRemovel = selection;
      selectionAfterRemovel = selectionAfterRemovel.filter(
        (current) => current.id === item.id
      );
      setSelection([...selectionAfterRemovel]);
    }
  }
  function isItemInTheSelection(item){
    if(selection.some(current=>current.id===item.id)){
      return true
    }
    return false

  }
  return (
    <div className="dd-wrapper">
      <div
        tabIndex={0}
        className="dd-header"
        role="button"
        onKeyPress={() => toggle(!open)}
        onClick={() => toggle(!open)}
      >
        <div className="dd-header__title">
          <p className="dd-header__title--bold">{title}</p>
        </div>
        <div className="dd-header__action">
          <p>{open ? "Close" : "open"}</p>
        </div>
      </div>
      {open &
      (
        <ul className="dd-list">
          {items.map((item) => {
            return (
              <li className="dd-list-item" key={item.id}>
                <button type="button" onClick={() => handleOnClick(item)}>
                  <span>{item.value}</span>
            <span>{isItemInTheSelection(item)&&'selected'}</span>
                </button>
              </li>
            );
          })}
        </ul>
      )}
      <div></div>
    </div>
  );
}
const clickOutsideConfig={
  handleClikOutside:()=>Dropdown.handleClikOutside,
}

export default onClickOutside(Dropdown, clickOutsideConfig) ;
