import React, { useEffect, useState } from "react";
import { PayPalButton } from "react-paypal-button-v2";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import LoadingBox from "../Components/LoadingBox";
import MessageBox from "../Components/MessageBox";
import { detailsOrder } from "../Actions/orderAction";
import Axios from "axios";

export default function Order(props) {
  const orderId = props.match.params.id;
  const [sdkReady, setSdkReady] = useState(false);
  const orderDetails = useSelector((state) => state.orderDetails);
  const { order, loading, error } = orderDetails;
  const dispatch = useDispatch();

  useEffect(() => {
    const addPayPalScript = async () => {
      const { data } = await Axios.get("/api/config/paypal");
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = `https://www.paypal.com/sdk/js?client-id=${data}`;
      script.async = true;
      script.onload = () => {
        setSdkReady(true);
      };
      document.body.appendChild(script);
    };
    if (!orderId) {
      dispatch(detailsOrder(orderId));
    } else {
      if (!order.isPaid) {
        if (!window.paypal) {
          addPayPalScript();
        } else {
          setSdkReady(true);
        }
      }
    }
  }, [dispatch, orderId, order, order.isPaid, sdkReady]);

  const successPaymentHandler = () => {
    //TODO
  };
  return loading ? (
    <LoadingBox></LoadingBox>
  ) : error ? (
    <MessageBox variant="danger">{error}</MessageBox>
  ) : (
    <>
      <div className="authContainer">
        <div className="form2">
          <div>
            <div>
              <h3>Order {order._id}</h3>
              <p>
                <strong>Name: </strong>
                {order.shippingAddress.fullName}
                <br />
                <strong>Address: </strong>
                {order.shippingAddress.address},{order.shippingAddress.city},
                {order.shippingAddress.postalCode},
                {order.shippingAddress.country}
              </p>
              {order.isDelivered ? (
                <MessageBox variant="success">
                  Delivered at {order.deliveredAt}
                </MessageBox>
              ) : (
                <MessageBox variant="danger">Not Delivered</MessageBox>
              )}
            </div>

            <div>
              <h3>Payment</h3>
              <p>
                <strong>Method: </strong>
                {order.paymentMethod}
              </p>
              {order.isPaid ? (
                <MessageBox variant="success">
                  Paid at {order.paidAt}
                </MessageBox>
              ) : (
                <MessageBox variant="danger">Not Paid yet</MessageBox>
              )}
            </div>

            <div>
              <h3>Order Items</h3>
              <ul>
                {order.orderItems.map((item) => (
                  <li key={item.product}>
                    <div className="row">
                      <div className="smallCart">
                        <img
                          src={`${process.env.PUBLIC_URL}/images/${item.image}`}
                          alt={item.name}
                        ></img>
                      </div>
                      <div className="min-30">
                        <Link to={`/Shop/${item.product}`}>
                          <p>{item.name}</p>
                        </Link>
                      </div>

                      <div>
                        <p>{item.price} €</p>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>

            <div>
              <ul>
                <li>
                  <h3>Order summary</h3>
                </li>
                <li>
                  <div>
                    <p>Items</p>
                  </div>

                  <div>
                    <p>{order.itemPrice.toFixed(2)} €</p>
                  </div>
                </li>
                <li>
                  <div>
                    <p>Shipping</p>
                  </div>
                  <div>
                    <p>{order.shippingPrice.toFixed(2)} €</p>
                  </div>
                </li>
                <li>
                  <div>
                    <p>Tax</p>
                  </div>

                  <div>
                    <p>{order.taxPrice.toFixed(2)} €</p>
                  </div>
                </li>
                <li>
                  <div>
                    <p>Total</p>
                  </div>

                  <div>
                    <p>{order.totalPrice.toFixed(2)} €</p>
                  </div>
                </li>
                {!order.isPaid && (
                  <li>
                    {!sdkReady ? (
                      <LoadingBox></LoadingBox>
                    ) : (
                      <PayPalButton
                        amount={order.totalPrice}
                        onSuccess={successPaymentHandler}
                      ></PayPalButton>
                    )}
                  </li>
                )}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
