import React, { Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";



export default function Category(props) {
  const { getPhotos } = useContext(MyContext);
  const { register, handleSubmit, errors } = useForm(); // initialize the hook
  const onSubmit = async (data) => {
    if (data.image && data.image.length > 0) {
      data.image = data.image[0];
    }
    const formData = new FormData();
    for (let key in data) {
      formData.append(key, data[key]);
    }

    const res = await fetch("http://localhost:5000/api/Shop", {
      method: "POST",
      body: formData,
    });
    const fromApi = await res.json();
    alert(JSON.stringify(res));
    console.log(fromApi);
  };
  const Delete = (url) => {
    //to be implemented
  };


  return (

    <Fragment>
    <div className="soulsContainer">
    <h1 className="VisionTitle">photo in <span style={{ color: "red" }} >Album</span> <br></br> click on one photo to delete </h1>
    <input
              name="image"
              ref={register}
              required
              type="file"
              name="image"
            />
            {errors.image && <p>{errors.image.message}</p>}
            <br></br>
    <button type="submit"><h3>Add photo</h3></button>
      {getPhotos.map((item) => {
        if (item.width_m <= 357) {
          return (
            <img
              onClick={() => Delete(item.url_l)}
              classname="soulsItems"
              src={item.url_m}
              key={item.id}
              image={item.url_l}
            />
          );
        }
      })}
    </div>
  </Fragment>
  );
}
