import React from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";
/* import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup"; */

/* const schema = yup.object().shape({
  albumName: yup.string().required(),
  image: yup
    .mixed()
    .required("you need to provide a file")
    .test("filesize", "The file is too large", (value) => {
      return value && value[0].size <= 200000;
    }),
  category: yup.string(),
  description: yup.string(),
}); */

export default function Phototup() {
  const { register, handleSubmit, errors } = useForm(/* {
    resolver: yupResolver(schema),
  } */); // initialize the hook

  const onSubmit = async (data) => {
    const formData = new FormData();
    formData.append("name", data.name[0]);


    const res = await fetch("http://localhost:5000/api/photosupload", {
      method: "POST",
      body: formData,
    }).then((res) => res.json());
    alert(JSON.stringify(res));
    console.log(formData);
  };

  return (
    <Fade bottom cascade={true}>
      <div className="authContainer">
        <div className="form2">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label>
              <h3> Album Name</h3>
            </label>
            <input type="text" name="albumName" ref={register({ required: true })} />
            {errors.name && "Name of the Album is required."}
            <label>
              <h3>Category</h3>
            </label>
            <input type="text" name="category" ref={register({ required: true })} />
            {errors.category && "Category of the Album is required."}
            <label>
              <h3>Description</h3>
            </label>
            <input
              type="text"
              name="description"
              ref={register({ required: true })}
            />
            {errors.description && "Description is required."}
            <label>
              <h3>Upload</h3>
            </label>
            <input
              name="image"
              ref={register}
              required
              type="file"
              name="image"
            />
            {errors.image && <p>{errors.image.message}</p>}
            <br></br>
            <button>
              <h3>Submit</h3>
            </button>
          </form>
        </div>
      </div>
    </Fade>
  );
}
