import React from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";

export default function Gallery() {
  const { register, handleSubmit, errors } = useForm(); // initialize the hook

  const onSubmit = async (data) => {
    const formData = new FormData();
    formData.append("name", data.name[0]);
    /* formData.append("date", data.date[0]);
    formData.append("category", data.category[0]);
    formData.append("description", data.description[0]);
    formData.append("countInStock", data.countInStock[0]);
    formData.append("rating", data.rating[0]);
    formData.append("numReviews", data.numReviews[0]);
    formData.append("image", data.image[0]); */

    const res = await fetch("http://localhost:5000/api/productsupload", {
      method: "POST",
      body: formData,
    }).then((res) => res.json());
    alert(JSON.stringify(res));
  };

  return (
    <Fade bottom cascade={true}>
      <div className="authContainer">
        <div className="form2">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label>
              <h3>Name</h3>
            </label>
            <input name="name" ref={register({ required: true })} />
            <label>
              <h3>Category</h3>
            </label>
            <input name="category" ref={register} />
            <label>
              <h3>Description</h3>
            </label>
            <input name="category" ref={register({ required: true })} />
            <label>
              <h3>Price</h3>
            </label>
            <input name="price" ref={register({ required: true })} />
            <label>
              <h3>In Stock</h3>
            </label>
            <input name="countInStock" ref={register({ required: true })} />
            <label>
              <h3>Rating</h3>
            </label>
            <input name="rating" ref={register} />
            {errors.name && "Name is required."}
            <label>
              <h3>Upload</h3>
            </label>
            <input
              name="image"
              ref={register}
              required
              type="file"
              name="image"
            />
            {errors.age && "Please enter number for age."}
            <br></br>
            <button>
              <h3>Submit</h3>
            </button>
          </form>
        </div>
      </div>
    </Fade>
  );
}
