import React, {useContext}from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";
import MyContext from "../Context/MyContext";

import '../App.scss';

export default function CreateAlbum(props) {
  const { register, handleSubmit, errors } = useForm(); // initialize the hook
  const {newAlbum, setNewAlbum } = useContext(MyContext);

  const onSubmit = async (data) => {

    /* props.history.push(`/albumedit/`) */
   /*  if (data.image && data.image.length > 0) {
      data.image = data.image[0];
    } */
    const formData = new FormData();
    for (let key in data) {
      formData.append(key, data[key]);
    }

    const res = await fetch("http://localhost:5000/api/albumup", {
      method: "POST",
      body: formData,
    }); 

    const album = await res.json();
    setNewAlbum(album)


  };

  return (
    <Fade bottom cascade={true}>
      <div className="authContainer">
        <div className="form2">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label>
              <h3>Name</h3>
            </label>
            <input type="text" name="albumName" ref={register({ required: true })} />
            {errors.name && "Name is required."}
            <label>
                {/* It has to be defaoult */}
              <h3>Category</h3>
            </label>
            {/* <input type="text" name="category" ref={register} /> */}
           <select id='categories' name="categories" ref={register}>
             <option value='home'><h3>home</h3></option>
             <option value='fashion'><h3>fashion</h3></option>
             <option value='outdoors'><h3>outdoors</h3></option>
             <option value='souls'><h3>souls</h3></option>
             <option value='projects'><h3>projects</h3></option>
           </select>

            <label>
              <h3>Description</h3>
            </label>
            <textarea
              type="text"
              name="description"
              ref={register({ required: true })}
            />
            {errors.description && "Description is required."}

            <br></br>
            <button type="submit"  >
              <h3>Add photos</h3>
            </button>
          </form>
        </div>
      </div>
    </Fade>
  );
}
