import React, { useEffect, Fragment, useContext, useState } from "react";
import MyContext from "../Context/MyContext";
import { useParams } from "react-router-dom";
/* import { ModalContext } from "./modalContext"; */

import Modal from "react-modal";

export default function OutdoorShow() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedImg, setSelectedImg] = useState(null)
  const { getAlbum, setGetAlbum } = useContext(MyContext);
  const { id } = useParams();
  console.log(id);

  /* let { handleModal } = React.useContext(ModalContext); */

  useEffect(() => {
    const getResult = async () => {
      try {
        const url = `http://localhost:5000/api/albumup/${id}`;
        const res = await fetch(url);
        console.log({ url });
        const album = await res.json();
        console.log(album);
        setGetAlbum(album);
      } catch (err) {}
    };
    getResult();
  }, []);
  console.log(getAlbum);
  /* const OpenModal= (url) => {
    handleModal(url);
    window.scrollTo(0, 0);
  }; */
  return (
    <Fragment>
      <Modal 
      isOpen={isOpen} 
      onRequestClose={() => setIsOpen(false)}
      style={
        {
          overlay:{
            backgroundColor: 'rgba(0, 0, 0, 0.712);'
          },
          content:{
            backgroundColor: 'rgba(10, 7, 7, 0.753)'
          }
        }
      }
      >
        <button onClick={() => setIsOpen(false)}>
          <h3>Close</h3>
        </button>
        <img src={selectedImg}/>

      </Modal>
      <div>
        <h1 className="VisionTitle">{getAlbum && getAlbum.albumName}</h1>
        <br></br>
        <div className="soulsContainer">
          {getAlbum.images.map((item) => {
            return (
              <img
                src={item}
                key={item}
                onClick={() => setIsOpen(true),() => setSelectedImg(item)}
                classname="soulsItems"
              />
            );
          })}
        </div>
      </div>
    </Fragment>
  );
}
