import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import dataShop from "../db/dataShop";
import Rating from "./Rating";

export default function productScreen(props) {
  const product = dataShop.products.find(
    (x) => x._id === props.match.params.id //this value is the one the user inserts
  ); 
  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <Fragment>
    <Link to='/Shop'><h2>Back to the Shop</h2></Link>
    <div className="row top">
      <div className="col-2">
        <img className="large" 
        src={`${process.env.PUBLIC_URL}/images/${product.image}`} 
        alt={product.name} />
      </div>
      <div className="col-1">
        <ul>
          <li><h2>{product.name}</h2></li>
          <li>
            <Rating
              rating={product.rating}
              numReviews={product.numReviews}
            ></Rating>
          </li>
          <li><h2>Price:{product.price}</h2></li>
          <li>
           <h2>Description:</h2> 
            <p>{product.description}</p>
          </li>
        </ul>
      </div>
      <div className="col-1">
        <div className="card card-body">
          <ul>
            <li>
              <div className="row">
                <div><h2>Price</h2></div>

                <div className="price"><h2>{product.price}</h2></div>
              </div>
            </li>
            <li>
              <div className="row">
                <div><h2>Status: </h2></div>

                <div>
                  {product.countInStock > 0 ? (
                    <span className="success"><h2>In stock</h2></span>
                  ) : (
                    <span className="error"><h2>Unavailable</h2></span>
                  )}
                </div>
              </div>
            </li>
            <li>
              <button className="primary block"><h2>Add to Cart</h2></button>
            </li>
          </ul>
        </div>
      </div>
    </div>
    </Fragment>
  );
}
