import React, { useEffect, Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { useLocation } from "react-router-dom";
import { ModalContext } from "./modalContext";
/* import useModal from "./useModal";
import Component from "./Component";
import ScrollToTheTop from "./ScrollToTop"; */

export default function FashionShow() {
  const { getData, setGetData } = useContext(MyContext);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const id = params.get("name");
  let { handleModal } = React.useContext(ModalContext);

  useEffect(() => {
    const getResult = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/albumup/${id}`);
        const album = await res.json();
        console.log(album);
        setGetData(album);
      } catch (err) {}
    };
    getResult();
  }, []);
  const ScrollUp = (url) => {
    handleModal(url);
    window.scrollTo(0, 0);
  };
  return (
    <Fragment>

            <div>
              <h1 className="VisionTitle">
                {getData.albumName}
              </h1>
              <br></br>


                {getData.images.map((item) => {
                  return  <div className="soulsContainer">
                    <img
                      src={item}
                      key={item}
                      onClick={() => ScrollUp(item)}
                      classname="soulsItems"
                    />
                    </div>

                })}

            </div>
          );


    </Fragment>
  );
}
