import React, { useState, Fragment } from "react";
import { Link } from "react-router-dom";
import { FaInstagram } from "react-icons/fa";
import * as FaIcons from "react-icons/fa";
import * as AiIcons from "react-icons/ai";
import Fade from "react-reveal/Fade";
import { useDispatch, useSelector } from "react-redux";
import { signout } from "../Actions/signAction";

export default function Navigation() {
  const [show, setShow] = useState(false);
  const [sidebar, setSidebar] = useState(false);
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;
  const userSignIn = useSelector((state) => state.userSignin);
  const { userInfo } = userSignIn;
  const dispatch = useDispatch();
  const signoutHandeler = () => {
    dispatch(signout());
  };
  const showSidebar = () => setSidebar(!sidebar);
  return (
    <Fragment>
      {/* <Fade bottom cascade={true}> */}

      <Link to="#" className="menu-bars">
        <div className="NavBarContainer">
          <div className="NavBar">
            <FaIcons.FaBars onClick={showSidebar} />
          </div>
        </div>
      </Link>

      <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
        <Link to="#" style={{ textDecoration: "none" }}>
          <div>
            <div>
              <AiIcons.AiOutlineClose onClick={showSidebar}/>
            </div>
          </div>
        </Link>
        <Link to="/" style={{ textDecoration: "none" }}>
          <div>
            <div>Home</div>
          </div>
        </Link>

        <Link to="/About" style={{ textDecoration: "none" }}>
          <div>
            <div>About</div>
          </div>
        </Link>

        <Link
          to="#"
          style={{ textDecoration: "none" }}
          onClick={() => setShow(!show)}
        >
          <div /* className="dropdown" */>
            <div onClick={() => setShow(!show)}>
              Photography <i className="fa fa-caret-down"></i>{" "}
            </div>
            {show && (
              <div className="subNav">
                <Link to="/Fashion" style={{ textDecoration: "none" }}>
                  <div>Fashion</div>
                </Link>
                <Link to="/Visions" style={{ textDecoration: "none" }}>
                  <div>Visions</div>
                </Link>
                <Link to="/SoulsInABox" style={{ textDecoration: "none" }}>
                  <div>Souls in a Box</div>
                </Link>
              </div>
            )}
          </div>
        </Link>

        <div>
          <Link to="/Shop" style={{ textDecoration: "none" }}>
            <div>On-Line Shop</div>
          </Link>
          <Link to="/Cart" style={{ textDecoration: "none" }}>
            {cartItems.length > 0 && (
              <div className="badge">
                <p>{cartItems.length} in Cart</p>
              </div>
            )}
          </Link>
        </div>

        <Link to="/Collaborations" style={{ textDecoration: "none" }}>
          <div>
            <div>Collaborations</div>
          </div>
        </Link>

        <Link to="/Contacts" style={{ textDecoration: "none" }}>
          <div>
            <div>Contacts</div>
          </div>
        </Link>
        {userInfo ? (
          <div>
            <div className="dropdown">
              <Link to="#" style={{ color: "gold" }}>
                {userInfo.name} <i className="fa fa-caret-down"></i>{" "}
              </Link>
              <ul className="dropdown-content">
                <Link to="#signout" onClick={signoutHandeler}>
                  Sign Out
                </Link>
              </ul>
            </div>
          </div>
        ) : (
          <Link to="/SignIn" style={{ textDecoration: "none" }}>
            <div>
              <div>Sign In</div>
            </div>
          </Link>
        )}
        <div>
          <div className="instaIcon" style={text-a}>
            <FaInstagram />
          </div>
        </div>
      </nav>
      {/* </Fade> */}
    </Fragment>
  );
}
