import React, { Fragment, useEffect, useContext } from "react";
import { useLocation } from "react-router-dom";
import Fade from "react-reveal/Fade";
import MyContext from "../Context/MyContext.js";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

export default function Category(props) {
  const { getData, setGetData } = useContext(MyContext);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const name = params.get("name");
  console.log(name);
  useEffect(async () => {
    const res = await fetch(
      `http://localhost:5000/api/albumup/bycategory?name=${name}`
    );
    const albums = await res.json();
    setGetData(albums);
    console.log(albums);
  }, []);

  const goToAlbum = () => {
    props.history.push(`/albumedit/`);
  };
  const goToCreateAlbum = () => {
    props.history.push(`/createalbum/`);
  };
  const responsive = {
    desktop: {
      breakpoint: { max: 2500, min: 1024 },
      items: 5,
      slidesToSlide: 1, // optional, default to 1.
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 4,
      slidesToSlide: 1, // optional, default to 1.
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 2,
      slidesToSlide: 1, // optional, default to 1.
    },
  };
  return (
    <Fragment>
      <Fade bottom cascade={true}>
        <div onClick={goToAlbum}>
          <h1 className="VisionTitle">
            Albums in <span style={{ color: "red" }}>{name}</span> category:
            <br></br> click on the album to edit{" "}
          </h1>
        </div>
        <div
          style={{
            width: "100vw",
            display: "flex",
            flexFlow: "row",
            justifyContent: "center",
          }}
        >
          <button onClick={goToCreateAlbum} style={{ width: "30%" }}>
            <h3>Add a new Album</h3>
          </button>
        </div>
        {getData &&
          getData.albumName.map((item) => {
            return (
              <div className="visionsContainer">
                <div className="visionsDescriptions">
                  <h3>{item}</h3>
                </div>
              </div>
            );
          })}
        {/* {getData &&
          getData.map(() => {
            return (
              <div className="visionsContainer" onClick={goToAlbum}>
                <div className="visionsDescriptions">
                  <h3>{getData.albumName}</h3>

                  <Carousel
                    swipeable={true}
                    draggable={true}
                    showDots={false}
                    responsive={responsive}
                    infinite={true}
                    autoPlay={true}
                    autoPlaySpeed={1500}
                    keyBoardControl={true}
                    customTransition="all .5"
                    transitionDuration={500}
                    containerClass="carousel-container2"
                    removeArrowOnDeviceType={["tablet", "mobile", "desktop"]}
                    dotListClass="custom-dot-list-style"
                    itemClass="carousel-item-padding-40-px"
                  >
                    {getData.images.map((url) => {

                        return <img src={url} key={url} />;

                    })}
                  </Carousel>
                </div>
              </div>
            );
          })} */}
        ;
      </Fade>
    </Fragment>
  );
}
