import React from "react";
import { useForm } from "react-hook-form";
import Fade from "react-reveal/Fade";
/* import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup"; */

/* const schema = yup.object().shape({
  name: yup.string().required(),
  image: yup
    .mixed()
    .required("you need to provide a file")
    .test("filesize", "The file is too large", (value) => {
      return value && value[0].size <= 200000;
    }),
  date: yup.date(),
  category: yup.string(),
  description: yup.string(),
  price: yup.number().required(),
  countInStock: yup.number().required(),
  rating: yup.number(),
  numReviews: yup.number(),
}); */

export default function Productup() {
  const { register, handleSubmit, errors } = useForm(/* {
    resolver: yupResolver(schema),
  } */); // initialize the hook

  const onSubmit = async (data) => {
    const formData = new FormData();
    formData.append("name", data.name[0]);


    const res = await fetch("http://localhost:5000/api/productsupload", {
      method: "POST",
      body: formData,
    }).then((res) => res.json());
    alert(JSON.stringify(res));
    console.log(formData);
  };

  return (
    <Fade bottom cascade={true}>
      <div className="authContainer">
        <div className="form2">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label>
              <h3>Name</h3>
            </label>
            <input type="text" name="name" ref={register({ required: true })} />
            {errors.name && "Name is required."}
            <label>
              <h3>Category</h3>
            </label>
            <input type="text" name="category" ref={register} />
            <label>
              <h3>Description</h3>
            </label>
            <input
              type="text"
              name="category"
              ref={register({ required: true })}
            />
            {errors.description && "Description is required."}
            <label>
              <h3>Price</h3>
            </label>
            <input
              type="text"
              name="price"
              ref={register({ required: true })}
            />
            <label>
              <h3>In Stock</h3>
            </label>
            <input
              type="text"
              name="countInStock"
              ref={register({ required: true })}
            />
            {errors.name && "How many products are in Stock?"}
            <label>
              <h3>Rating</h3>
            </label>
            <input type="text" name="rating" ref={register} />

            <label>
              <h3>Upload</h3>
            </label>
            <input
              name="image"
              ref={register}
              required
              type="file"
              name="image"
            />
            {errors.image && <p>{errors.image.message}</p>}
            <br></br>
            <button>
              <h3>Submit</h3>
            </button>
          </form>
        </div>
      </div>
    </Fade>
  );
}
