import React, { useState, useEffect, Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { ModalContext } from "./modalContext";
import Fade from "react-reveal/Fade";
import useModal from "./useModal";
import Component from "./Component";
import ScrollToTheTop from "./ScrollToTop";

export default function SoulsInABox() {
  const { getPhotos } = useContext(MyContext);
  let { handleModal } = React.useContext(ModalContext);
  const { getData, setGetData } = useContext(MyContext);
  useEffect(() => {
    const getResult = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/albumup/bycategory?name=${"souls"}`
        );
        const albums = await res.json();
        console.log(albums);
        setGetData(albums);
      } catch (err) {}
    };
    getResult();
  }, []);
  const ScrollUp = (url) => {
    handleModal(url);
    window.scrollTo(0, 0);
  };
  return (
    <Fade bottom cascade={true}>
      <div className="soulsBigContainer">
        {getData &&
          getData.map((album) => {
            return (
              <div>
                <h2 className="soulsTitle">Souls In A Box</h2>
                <p className="soulsText">{album.description}</p>
              </div>
            );
          })}

        {getData &&
          getData.map((album) => {
            album.images.map((url) => {
              return (
                <div className="soulsContainer">
                  <img
                    onClick={() => ScrollUp(url)}
                    classname="soulsItems"
                    src={url}
                    key={url._id}
                  />
                </div>
              );
            });
          })}
      </div>
    </Fade>
  );
}
