import React, { Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { useForm } from "react-hook-form";

export default function AlbumEdit(props) {
  const { newAlbum, setGetPhotos, setNewAlbum, getPhotos } = useContext(MyContext);
  const { register, handleSubmit } = useForm(); // initialize the hook

  console.log({ newAlbum });

  const onSubmit = async (data) => {
    const formData = new FormData();
    console.log({ data });
    for (let i = 0 ; i < data.images.length ; i++) {
      formData.append("images", data.images[i]);
    }


   /*  formData.append("images", data.images); */
    /*  for (const image of data.image) {
      formData.append('image[]', image)
   } */
    /* const data={ ...images, ...newAlbum} */
    for (let key in newAlbum) {
      formData.append(key, newAlbum[key]);

    }
    /* console.log({data}) */
    const res = await fetch("http://localhost:5000/api/albumup", {
      method: "POST",
      body: formData,
    });
    const fromApi = await res.json();
    console.log(fromApi)

   setNewAlbum({...newAlbum, images: fromApi.createdAlbum.images })
  };
  const Delete = (url) => {
    //to be implemented
  };
  const DeleteAlbum = (url) => {
    //to be implemented
    props.history.goBack();
  };

  return (
    <Fragment>
      <div style={{ textAlign: "center" }}>
        <h1 className="VisionTitle">
          photos in <span style={{ color: "red" }}>Album</span> <br></br> click
          on one photo to delete{" "}
        </h1>

        <br></br>
        <form onSubmit={handleSubmit(onSubmit)}>
          <button type="submit">
            <h3>Add photo</h3>
          </button>
          <button type="submit" onClick={() => DeleteAlbum()}>
            <h3>Delete Albim</h3>
          </button>

          {/* <div style={{margin:"auto", }}> */}
          <input
            name="images"
            ref={register}
            required
            type="file"
            multiple
          />
          {/* </div> */}
        </form>
      </div>
      <div className="soulsContainer">
        {getPhotos.map((item) => {
          /* if (item.width_m <= 357) { */
            return (
              <img
                onClick={() => Delete(item)}
                classname="soulsItems"
                src={item.images}
                key={item.category}
                /* image={item.url_l} */
              />
            );
          /* } */
        })}
      </div>
    </Fragment>
  );
}
