import React, { Component } from 'react'
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"

export default class Contacts extends Component {
    state={
        name:"",
        email:"",
        message:"",
        disabled: false,
        emailsent:null,

    }
    handleChange=(event)=>{
        const target=event.target;
        const value =target.type==="checkbox"? target.checked:target.value;
        const name= target.name;

        this.setState({
            [name]:value
        })
    }
        handleSubmit= (event) =>{
            event.preventDefault();
            this.setState({
                disabled:true,
                emailsent:false,


            })
        }

    render() {
        return (
            <div className="contactContainer">
              <Form onSubmit={this.handleSubmit}>
                  <Form.Group>
                      <Form.Label htmlFor="Full-name"><h2 className="contactsLabels">Full Name</h2></Form.Label>
                      <Form.Control id="Full-name" name="name" type="text" value={this.state.name} onChange={this.handleChange}/>
                  </Form.Group>
                  <Form.Group>
                      <Form.Label htmlFor="email"><h2 className="contactsLabels">Email</h2></Form.Label>
                      <Form.Control id="email" name="email" type="email" value={this.state.email} onChange={this.handleChange}/>
                  </Form.Group>
                  <Form.Group>
                      <Form.Label htmlFor="message"><h2 className="contactsLabels">Message</h2></Form.Label>
                      <Form.Control id="message" name="message" as="textarea" rows="6" value={this.state.message} onChange={this.handleChange}/>
                  </Form.Group>

                  <Button className="d-inline-block" variant="primary" type="submit" disabled={this.state.disabled}>
                      <p>Send</p>
                      </Button>

              {this.state.emailsent===true && <p className="d-inline success-msg">Email Sent</p>}
              {this.state.emailsent===false && <p className="d-inline err-msg">Email Not Sent</p>}


              </Form>
            </div> 
        )
    }
}
