import React, { Fragment, useContext } from "react";
import MyContext from "../Context/MyContext";
import { useForm } from "react-hook-form";

export default function AlbumEdit(props) {
  const { newAlbum, setNewAlbum, getPhotos } = useContext(MyContext);
  const { register, handleSubmit } = useForm(); // initialize the hook

  console.log({newAlbum})

  const onSubmit = async (data) => {
    const formData = new FormData();
console.log({data})
    for (const image of data.images) {
      formData.append('image[]', image)
   }
    /* const data={ ...images, ...newAlbum} */
    for (let key in newAlbum) {
      formData.append(key, newAlbum[key]);
    }
    /* console.log({data}) */
    const res = await fetch("http://localhost:5000/api/albumup", {
      method: "POST",
      body: formData,
    });
    const fromApi = await res.json();
    alert(JSON.stringify(res));
    console.log(fromApi);
  };
  const Delete = (url) => {
    //to be implemented
  };
  const DeleteAlbum = (url) => {
    //to be implemented
    props.history.goBack()
  };

  return (
    <Fragment>
      <div style={{textAlign:"center"}}>
        <h1 className="VisionTitle">
          photos in <span style={{ color: "red" }}>Album</span> <br></br> click
          on one photo to delete{" "}
        </h1>



        <br></br>
        <form onSubmit={handleSubmit(onSubmit)}>
        <button type="submit">
          <h3>Add photo</h3>
        </button>
        <button type="submit" onClick={() => DeleteAlbum()}>
          <h3>Delete Albim</h3>
        </button>

        {/* <div style={{margin:"auto", }}> */}
        <input name="image" ref={register} required type="file" name="image" />
        {/* </div> */}
        </form>
      </div>
      <div className="soulsContainer">
        {getPhotos.map((item) => {
          if (item.width_m <= 357) {
            return (
              <img
                onClick={() => Delete(item.url_l)}
                classname="soulsItems"
                src={item.url_m}
                key={item.id}
                image={item.url_l}
              />
            );
          }
        })}
      </div>
    </Fragment>
  );
}
