import React, { useState, Fragment, useEffect, useContext } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Navigation from "./Components/Navigation";
import "./App.scss";
import About from "./Components/About";
import Collaborations from "./Components/Collaborations";
import Contacts from "./Components/Contacts";
import Visions from "./Components/Visions";
import SoulsInABox from "./Components/SoulsInABox";
import Fashion from "./Components/Fashion";
import Lightining from "./video/lightiningbolt.mp4";
import FashionShow from "./Components/FashionShow";
import VisionsShow from "./Components/VisionsShow";
import Home from "./Components/Home";
import Shop from "./Components/Shop";
import MyProvider from "./Context/MyProvider";
import PhotographyCarousel from "./Components/PhotographyCarousel";
import {
  ModalContext,
  ModalProvider,
  handleModal,
} from "./Components/modalContext";
import ScrollToTheTop from "./Components/ScrollToTop";
import { motion } from "framer-motion";

export default function App() {
  const [show, setShow] = useState(false);
  const [showIcon, setShowIcon] = useState(false);

  const enterVariant={
    opened:{fontSize: "5vw", y: 100, textAlign: "center"},
    closed:{ visibility:"hidden" }
  }
  const logoVariant={
    opened:{ fontSize: "10vw", y: 200, textAlign: "center" },
    closed:{y:0}
  }



  return (
    <MyProvider>
      <ModalProvider>
        <Router>
          <ScrollToTheTop />
       {/*    <div className="App"> */}
             {/*  <div className="logo"> */}
                <motion.h2
                className='enter'
                  initial={{ y: -250 }}
                  variants={enterVariant}
                  animate={show?"closed":"opened"}
                  onClick={() => setShow(!show)}
                >
                  Enter
                </motion.h2>
                <motion.h1
                  initial={{ y: -250 }}
                  variants={logoVariant}
                  animate={show?"closed":"opened"}
                  onClick={() => setShow(!show)}
                >
                  The Naked Moon
                </motion.h1>
            {/*  </div> */}


            {show ?(
              <Fragment>
                <Navigation />

                <Switch>
                  <Route exact path="/" component={Home} />
                  <Route path="/About" component={About} />
                  <Route exact path="/Fashion" component={Fashion} />
                  <Route exact path="/Visions" component={Visions} />
                  <Route exact path="/SoulsInABox" component={SoulsInABox} />
                  <Route exact path="/FashionShow" component={FashionShow} />
                  <Route exact path="/VisionsShow" component={VisionsShow} />
                  <Route
                    exact
                    path="/Collaborations"
                    component={Collaborations}
                  />
                  <Route exact path="/Shop" component={Shop} />
                  <Route exact path="/Contacts" component={Contacts} />
                </Switch>
              </Fragment>
            )  : (
             <video autoPlay loop muted >
                <source src={Lightining} type="video/mp4" />
              </video>
            ) } 
          {/* </div> */}
        </Router>
      </ModalProvider>
    </MyProvider>
  );
}
